import { Box } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";

const Header = () => {
  const isMobile = useMediaQuery("(max-width:700px)");

  const headerInfo = [
    `<Box display="flex" alignItems="center" gap="8px">
      <img
        alt="checkmark"
        src="checkmark.png"
        style={{ width: "22px", height: "22px" }}
      />
      <Typography fontSize="12px" fontWeight="500" color="#FFFFFF">
        30-DAY SATISFACTION GUARANTEE
      </Typography>
    </Box>`,
    `<Box display="flex" alignItems="center" gap="8px">
    <img
      alt="truck"
      src="truck.png"
      style={{ width: "22px", height: "21px" }}
    />
    <Typography fontSize="12px" fontWeight="500" color="#FFFFFF">
      Free delivery on orders over $40.00
    </Typography>
    </Box>`,
    `<Box display="flex" alignItems="center" gap="8px">
    <img
      alt="heart"
      src="heart.png"
      style={{ width: "22px", height: "22px" }}
    />
    <Typography fontSize="12px" fontWeight="500" color="#FFFFFF">
      50.000+ HAPPY CUSTOMERS
    </Typography>
    </Box>`,
  ];

  return (
    <Box width="100%">
      <Box
        display="flex"
        height={isMobile ? "42px" : "50px"}
        bgcolor="#252F3D"
        justifyContent="space-around"
      >
        {headerInfo.map((info) => info)}
      </Box>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        height="96px"
        p={isMobile ? "0 20px 0 20px" : "0 127px 0 127px"}
      >
        <img
          alt="clarifion-logo"
          src="clarifion-logo.png"
          style={{
            width: isMobile ? "106.67px" : "192px",
            height: isMobile ? "20px" : "36px",
          }}
        />
        <Box height={isMobile ? "16px" : "32px"}>
          <img alt="mcafee-logo" src="mcafee.png" style={{ height: "100%" }} />
          <img alt="norton-logo" src="norton.png" style={{ height: "100%" }} />
        </Box>
      </Box>
    </Box>
  );
};

export default Header;
